package practicecodes;

public class SumOFArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int ch[]= {1,2,3};
		int sum=0;
		for (int i=0;i<=ch.length-1;i++) {
			sum=sum+ch[i];
			
		}
		System.out.println("The sum of the numbers in array is = " + sum);
		

	}

}
